DELIMITER $$
CREATE TRIGGER before_insert_notification
BEFORE INSERT ON notification
FOR EACH ROW
BEGIN
   SET NEW.NotiTime = DATE_ADD(NOW(), INTERVAL 7 HOUR);
END;
